from pybricks.hubs import PrimeHub
from pybricks.tools import wait

from bluepad import bluepad_init, color_rgb, fill_neopixel, gamepad, get_buttons, get_direction_pad, get_left_stick_horizontal, get_left_stick_vertical, get_right_stick_horizontal, get_right_stick_vertical, init_neopixel, set_neopixel, set_servo

# Set up all devices.
prime_hub = PrimeHub()

# Initialize variables.
servo = 0


# The main program starts here.
bluepad_init('A')
init_neopixel(1, 25)
while True:
    servo = int((get_left_stick_horizontal() + 100) * 179 / 200)
    print(servo)
    set_servo(2, servo)
    fill_neopixel(color_rgb(100 + get_left_stick_horizontal(), 100 + get_left_stick_vertical(), 0), 0.5)
    wait(20)
